query.statement:
SELECT <FIELDS/>
        		FROM <TABLE/>
        		WHERE <FIELD name='id'/> > ${start-id}
        		LIMIT ${batch-size}

				SELECT <FIELDS/>
        		FROM <TABLE/>
        		WHERE <FIELD name='id'/> = ${key-id}

SELECT <FIELDS/>
        		FROM <TABLE/>
        		WHERE <FIELD name='priority'/> = ${priority}
        		AND <FIELD name='group-id'/> = ${group-id}

UPDATE <TABLE/>
			        SET <FIELDS/>
			        WHERE <FIELD name='id'/> = ${id} AND <FIELD name='offset'/> < ${offset}

SELECT <FIELDS/>
        		FROM <TABLE/>
        		WHERE <FIELD name='id'/> > ${last-id} 
        		AND <FIELD name='id'/> <= ${max-id}
        		ORDER BY <FIELD name='id'/> ASC
        		LIMIT ${batch-size}

SELECT <FIELDS/>
        		FROM <TABLE/>
        		WHERE <FIELD name='id'/> = ${key-id}

SELECT <FIELDS/>
        		FROM <TABLE/>
        		WHERE <FIELD name='group-id'/> = ${group-id}

UPDATE <TABLE/>
			        SET <FIELDS/>
			        WHERE <FIELD name='id'/> = ${id}
	        		AND <FIELD name='last-id'/> < ${last-id}
